sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// If the testdate is before rightnow, return true, else return false
		if (testdate.before(rightnow)) {
			return true;
		}
		return false;
	},

	isDateUnavailable: function(strDate){
		// rightnow stores the current time
		var rightnow = new GlideDateTime();
		var earliestAvailable = new GlideDateTime();
		earliestAvailable.setDisplayValueInternal(rightnow.getLocalDate() + ' 00:00:00');
		earliestAvailable.addDaysLocalTime(1); //Earliest available date in Local Time/format

		var msg = "SI: Earliest Availability: " + earliestAvailable.getDisplayValueInternal();
		// Create a GlideDateTime object for the When needed date
		var whenNeeded = new GlideDateTime(strDate);
		//whenNeeded.setDisplayValue(strDate);

		msg += "\nWhenNeeded: " + whenNeeded.getDisplayValueInternal();

		gs.info(msg);

		// If the When needed date is before rightnow, do not write the record to the database
		// Output an error message to the screen
		if(whenNeeded.before(earliestAvailable)){
			return true;
		}
		return false;
	},	

    type: 'NeedItUtils'
};
