sysauto_script.script:
// Get today's time and date

try{
	var rightNow = new GlideDateTime();

	var upcomingNITasks = new GlideRecord('x_58872_needit_needit_task');
	upcomingNITasks.addQuery('due_date','>=', rightNow.getDisplayValue());
	rightNow.addDaysUTC(1);
	upcomingNITasks.addQuery('due_date','<=', rightNow.getDisplayValue());
	//overdueNITasks.addQuery('state','!=',3);
	gs.info("Upcoming NeedIt Tasks QUERY: " + upcomingNITasks.getEncodedQuery());
	upcomingNITasks.query();
	// Write a log message for each Upcoming NeedIt Task Record
	while(upcomingNITasks.next()){
		gs.info("Upcoming NeedIt Task record = " + upcomingNITasks.number);
		gs.eventQueue('x_58872_needit.dueSoonNITask',upcomingNITasks,upcomingNITasks.number,gs.getUserName());
	}
}
catch(e){
	gs.error("Upcoming NeedIt Tasks ERROR: " + e);

}
